import React, { useState } from "react";

const csOutcomes = [
  "Problem Solving",
  "Solution Development",
  "Communication",
  "Decision-Making",
  "Collaboration",
  "Application",
];

const WeeklyProgressReportForm = () => {
  const [formData, setFormData] = useState({
    tasks: "",
    challenges: "",
    lessons: "",
    week: "1",
    outcomes: [],
  });

  const [submittedReports, setSubmittedReports] = useState([]);

  const toggleOutcome = (outcome) => {
    setFormData((prev) => {
      const exists = prev.outcomes.includes(outcome);
      return {
        ...prev,
        outcomes: exists
          ? prev.outcomes.filter((o) => o !== outcome)
          : [...prev.outcomes, outcome],
      };
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.outcomes.length < 3) {
      alert("Please select at least 3 CS outcomes.");
      return;
    }
    setSubmittedReports([...submittedReports, formData]);
    setFormData({
      tasks: "",
      challenges: "",
      lessons: "",
      week: "1",
      outcomes: [],
    });
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-semibold text-center mb-4">Weekly Progress Report</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block font-medium">Tasks Performed</label>
          <textarea
            name="tasks"
            value={formData.tasks}
            onChange={handleChange}
            className="w-full border rounded p-2"
            required
          />
        </div>
        <div>
          <label className="block font-medium">Challenges Faced</label>
          <textarea
            name="challenges"
            value={formData.challenges}
            onChange={handleChange}
            className="w-full border rounded p-2"
            required
          />
        </div>
        <div>
          <label className="block font-medium">Lessons Learned</label>
          <textarea
            name="lessons"
            value={formData.lessons}
            onChange={handleChange}
            className="w-full border rounded p-2"
            required
          />
        </div>
        <div>
          <label className="block font-medium">Logbook Week</label>
          <select
            name="week"
            value={formData.week}
            onChange={handleChange}
            className="w-full border rounded p-2"
          >
            {[...Array(15)].map((_, i) => (
              <option key={i} value={i + 1}>{`Week ${i + 1}`}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block font-medium">Select CS Outcomes (min 3)</label>
          <div className="grid grid-cols-2 gap-2 mt-2">
            {csOutcomes.map((outcome) => (
              <label key={outcome} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={formData.outcomes.includes(outcome)}
                  onChange={() => toggleOutcome(outcome)}
                />
                <span>{outcome}</span>
              </label>
            ))}
          </div>
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit Report
        </button>
      </form>

      <div className="mt-10">
        <h3 className="text-xl font-semibold mb-2">Submitted Reports</h3>
        {submittedReports.length === 0 ? (
          <p className="text-gray-500">No reports submitted yet.</p>
        ) : (
          <ul className="space-y-2">
            {submittedReports.map((report, index) => (
              <li key={index} className="border p-3 rounded bg-gray-50">
                <strong>Week {report.week}</strong>
                <p><strong>Tasks:</strong> {report.tasks}</p>
                <p><strong>Challenges:</strong> {report.challenges}</p>
                <p><strong>Lessons:</strong> {report.lessons}</p>
                <p><strong>CS Outcomes:</strong> {report.outcomes.join(", ")}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default WeeklyProgressReportForm;
