import React, { useState } from "react";

const csOutcomes = [
  "Problem Solving",
  "Solution Development",
  "Communication",
  "Decision-Making",
  "Collaboration",
  "Application",
];

const WeeklyProgressReportForm = () => {
  const [formData, setFormData] = useState({
    tasks: "",
    challenges: "",
    lessons: "",
    week: "1",
    outcomes: [],
  });

  const [submittedReports, setSubmittedReports] = useState([]);

  const toggleOutcome = (outcome) => {
    setFormData((prev) => {
      const exists = prev.outcomes.includes(outcome);
      return {
        ...prev,
        outcomes: exists
          ? prev.outcomes.filter((o) => o !== outcome)
          : [...prev.outcomes, outcome],
      };
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.outcomes.length < 3) {
      alert("Please select at least 3 CS outcomes.");
      return;
    }
    setSubmittedReports([...submittedReports, formData]);
    setFormData({
      tasks: "",
      challenges: "",
      lessons: "",
      week: "1",
      outcomes: [],
    });
  };

  return (
    <div className="max-w-5xl mx-auto mt-10 bg-white p-8 rounded-xl shadow-lg">
      <h2 className="text-3xl font-bold text-center text-blue-700 mb-6">Weekly Progress Report</h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block font-semibold mb-1">Tasks Performed</label>
          <textarea
            name="tasks"
            value={formData.tasks}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Describe the tasks you completed this week..."
            rows={3}
            required
          />
        </div>

        <div>
          <label className="block font-semibold mb-1">Challenges Faced</label>
          <textarea
            name="challenges"
            value={formData.challenges}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-yellow-400"
            placeholder="Mention any difficulties you encountered..."
            rows={3}
            required
          />
        </div>

        <div>
          <label className="block font-semibold mb-1">Lessons Learned</label>
          <textarea
            name="lessons"
            value={formData.lessons}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-green-400"
            placeholder="What did you learn this week?"
            rows={3}
            required
          />
        </div>

        <div>
          <label className="block font-semibold mb-1">Logbook Week</label>
          <select
            name="week"
            value={formData.week}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-lg p-2 bg-white"
          >
            {[...Array(15)].map((_, i) => (
              <option key={i} value={i + 1}>
                Week {i + 1}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block font-semibold mb-1">Select CS Outcomes (min 3)</label>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mt-2">
            {csOutcomes.map((outcome) => (
              <label key={outcome} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={formData.outcomes.includes(outcome)}
                  onChange={() => toggleOutcome(outcome)}
                  className="accent-blue-600 w-4 h-4"
                />
                <span className="text-sm">{outcome}</span>
              </label>
            ))}
          </div>
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white font-medium px-6 py-2 rounded-lg hover:bg-blue-700 transition duration-200"
        >
          Submit Report
        </button>
      </form>

      {/* Submitted Reports */}
      <div className="mt-10 border-t pt-6">
        <h3 className="text-2xl font-semibold text-gray-700 mb-4">Submitted Reports</h3>
        {submittedReports.length === 0 ? (
          <p className="text-gray-500 italic">No reports submitted yet.</p>
        ) : (
          <div className="space-y-4">
            {submittedReports.map((report, index) => (
              <div key={index} className="border border-gray-300 rounded-lg p-4 bg-gray-50 shadow-sm">
                <p><strong className="text-blue-600">Week:</strong> {report.week}</p>
                <p><strong>Tasks:</strong> {report.tasks}</p>
                <p><strong>Challenges:</strong> {report.challenges}</p>
                <p><strong>Lessons:</strong> {report.lessons}</p>
                <p><strong>CS Outcomes:</strong> {report.outcomes.join(", ")}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default WeeklyProgressReportForm;
